import React, { useState, useEffect } from 'react';
import quizData from './quizData.json';
import './styles.css';

// é¸æŠè‚¢ã‚’ãƒ©ãƒ³ãƒ€ãƒ ã«ä¸¦ã¹æ›¿ãˆã‚‹é–¢æ•°
function shuffleArray(array) {
  return array.sort(() => 0.5 - Math.random());
}

// å…¨ã‚«ãƒ†ã‚´ãƒªãƒ¼ã‹ã‚‰å•é¡Œã‚’å–å¾—ã™ã‚‹é–¢æ•°
function getAllQuestions() {
  const allQuestions = [];
  Object.entries(quizData.categories).forEach(([categoryKey, category]) => {
    Object.entries(category.subcategories).forEach(([subcategoryKey, subcategory]) => {
      const questionsWithMeta = subcategory.questions.map(q => ({
        ...q,
        categoryName: category.name,
        subcategoryName: subcategory.name
      }));
      allQuestions.push(...questionsWithMeta);
    });
  });
  return shuffleArray(allQuestions).slice(0, 30);
}

// ãƒ©ãƒ³ã‚­ãƒ³ã‚°ã‚’ã‚µãƒ¼ãƒãƒ¼ã«ä¿å­˜ã™ã‚‹é–¢æ•°
const saveScoreToServer = async (scoreData) => {
  try {
    const response = await fetch('http://localhost:3001/api/rankings', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(scoreData)
    });
    const data = await response.json();
    return data;
  } catch (error) {
    console.error('ãƒ©ãƒ³ã‚­ãƒ³ã‚°ä¿å­˜ã‚¨ãƒ©ãƒ¼:', error);
    return null;
  }
};

// ãƒ©ãƒ³ã‚­ãƒ³ã‚°ã‚’ã‚µãƒ¼ãƒãƒ¼ã‹ã‚‰å–å¾—ã™ã‚‹é–¢æ•°
const fetchRankings = async (mode) => {
  try {
    const response = await fetch(`http://localhost:3001/api/rankings/${mode}`);
    const data = await response.json();
    return data;
  } catch (error) {
    console.error('ãƒ©ãƒ³ã‚­ãƒ³ã‚°å–å¾—ã‚¨ãƒ©ãƒ¼:', error);
    return [];
  }
};

function QuizApp() {
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [selectedSubcategory, setSelectedSubcategory] = useState(null);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [score, setScore] = useState(0);
  const [showScore, setShowScore] = useState(false);
  const [options, setOptions] = useState([]);
  const [questions, setQuestions] = useState([]);
  const [feedback, setFeedback] = useState(null);
  const [isAnswered, setIsAnswered] = useState(false);
  const [isQuizKingMode, setIsQuizKingMode] = useState(false);
  const [playerName, setPlayerName] = useState('');
  const [showRanking, setShowRanking] = useState(false);
  const [rankings, setRankings] = useState({
    normal: [],
    quizKing: []
  });
  const [answerHistory, setAnswerHistory] = useState([]);
  const [answerStats, setAnswerStats] = useState({});

  // ã‚µãƒ–ã‚«ãƒ†ã‚´ãƒªãƒ¼ãŒé¸æŠã•ã‚ŒãŸã¨ãã«å•é¡Œã‚’è¨­å®š
  useEffect(() => {
    if (selectedCategory && selectedSubcategory && 
        quizData.categories[selectedCategory]?.subcategories[selectedSubcategory]) {
      try {
        const subcategoryQuestions = quizData.categories[selectedCategory]
          .subcategories[selectedSubcategory].questions;
        if (subcategoryQuestions && subcategoryQuestions.length > 0) {
          const shuffledQuestions = shuffleArray([...subcategoryQuestions]).slice(0, 10);
          setQuestions(shuffledQuestions);
          setCurrentQuestionIndex(0);
          setScore(0);
          setShowScore(false);
          setFeedback(null);
          setIsAnswered(false);
          setIsQuizKingMode(false);
        } else {
          console.error('é¸æŠã•ã‚ŒãŸã‚µãƒ–ã‚«ãƒ†ã‚´ãƒªãƒ¼ã«å•é¡ŒãŒã‚ã‚Šã¾ã›ã‚“');
        }
      } catch (error) {
        console.error('å•é¡Œãƒ‡ãƒ¼ã‚¿ã®èª­ã¿è¾¼ã¿ã‚¨ãƒ©ãƒ¼:', error);
      }
    }
  }, [selectedCategory, selectedSubcategory]);

  // ãƒ©ãƒ³ã‚­ãƒ³ã‚°ãƒ‡ãƒ¼ã‚¿ã‚’èª­ã¿è¾¼ã‚€
  useEffect(() => {
    const loadRankings = async () => {
      const normalRankings = await fetchRankings('normal');
      const quizKingRankings = await fetchRankings('quizKing');
      setRankings({
        normal: normalRankings,
        quizKing: quizKingRankings
      });
    };
    loadRankings();
  }, [showRanking]);

  // å•é¡Œã®æ­£ç­”ç‡ã‚’å–å¾—
  const fetchAnswerStats = async () => {
    try {
      const response = await fetch('http://localhost:3001/api/answer-stats');
      const data = await response.json();
      const statsMap = {};
      data.forEach(stat => {
        statsMap[stat.question_text] = {
          total: stat.total_attempts,
          correct: stat.correct_attempts,
          percentage: stat.correct_percentage
        };
      });
      setAnswerStats(statsMap);
    } catch (error) {
      console.error('æ­£ç­”ç‡å–å¾—ã‚¨ãƒ©ãƒ¼:', error);
    }
  };

  // ã‚³ãƒ³ãƒãƒ¼ãƒãƒ³ãƒˆãƒã‚¦ãƒ³ãƒˆæ™‚ã«æ­£ç­”ç‡ã‚’å–å¾—
  useEffect(() => {
    fetchAnswerStats();
  }, []);

  // å›ç­”ãƒ‡ãƒ¼ã‚¿ã‚’ä¿å­˜
  const saveAnswerStat = async (question, isCorrect) => {
    try {
      await fetch('http://localhost:3001/api/answer-stats', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          category: isQuizKingMode ? questions[currentQuestionIndex].categoryName : quizData.categories[selectedCategory].name,
          subcategory: questions[currentQuestionIndex].subcategoryName,
          question: question,
          isCorrect: isCorrect
        })
      });
      // æ­£ç­”ç‡ã‚’å†å–å¾—
      fetchAnswerStats();
    } catch (error) {
      console.error('å›ç­”çµ±è¨ˆä¿å­˜ã‚¨ãƒ©ãƒ¼:', error);
    }
  };

  // ã‚¹ã‚³ã‚¢ã‚’ä¿å­˜ã™ã‚‹é–¢æ•°
  const saveScore = async () => {
    if (!playerName.trim()) return;

    const scoreData = {
      name: playerName,
      score: score,
      total: questions.length,
      category: isQuizKingMode ? 'ã‚¯ã‚¤ã‚ºç‹ãƒãƒ£ãƒ¬ãƒ³ã‚¸' : quizData.categories[selectedCategory].name,
      mode: isQuizKingMode ? 'quizKing' : 'normal'
    };

    const result = await saveScoreToServer(scoreData);
    if (result) {
      const updatedRankings = await fetchRankings(isQuizKingMode ? 'quizKing' : 'normal');
      setRankings(prev => ({
        ...prev,
        [isQuizKingMode ? 'quizKing' : 'normal']: updatedRankings
      }));
      setShowRanking(true);
    }
  };

  // ã‚¯ã‚¤ã‚ºç‹ãƒãƒ£ãƒ¬ãƒ³ã‚¸ãƒ¢ãƒ¼ãƒ‰é–‹å§‹
  const startQuizKingChallenge = () => {
    const allQuestions = getAllQuestions();
    setQuestions(allQuestions);
    setCurrentQuestionIndex(0);
    setScore(0);
    setShowScore(false);
    setFeedback(null);
    setIsAnswered(false);
    setIsQuizKingMode(true);
  };

  // å•é¡ŒãŒåˆ‡ã‚Šæ›¿ã‚ã‚‹ãŸã³ã«é¸æŠè‚¢ã®é †åºã‚’ã‚·ãƒ£ãƒƒãƒ•ãƒ«
  useEffect(() => {
    if (questions.length > 0 && currentQuestionIndex < questions.length) {
      const currentQuestion = questions[currentQuestionIndex];
      if (currentQuestion && currentQuestion.correct && currentQuestion.distractors) {
        const allOptions = shuffleArray([currentQuestion.correct, ...currentQuestion.distractors]);
        setOptions(allOptions);
        setFeedback(null);
        setIsAnswered(false);
      }
    }
  }, [currentQuestionIndex, questions]);

  // å›ç­”ãƒœã‚¿ãƒ³ã‚’ã‚¯ãƒªãƒƒã‚¯ã—ãŸã¨ãã®å‡¦ç†
  const handleAnswerOptionClick = async (selectedOption) => {
    if (!questions[currentQuestionIndex] || isAnswered) return;

    const currentQuestion = questions[currentQuestionIndex];
    const isCorrect = selectedOption === currentQuestion.correct;

    // å›ç­”ãƒ‡ãƒ¼ã‚¿ã‚’ä¿å­˜
    await saveAnswerStat(currentQuestion.question, isCorrect);

    // å›ç­”å±¥æ­´ã«è¿½åŠ 
    const newAnswer = {
      questionNumber: currentQuestionIndex + 1,
      question: currentQuestion.question,
      selectedAnswer: selectedOption,
      correctAnswer: currentQuestion.correct,
      isCorrect: isCorrect,
      category: isQuizKingMode ? currentQuestion.categoryName : quizData.categories[selectedCategory].name,
      subcategoryName: currentQuestion.subcategoryName
    };
    setAnswerHistory(prev => [...prev, newAnswer]);

    if (isCorrect) {
      setScore(prevScore => prevScore + 1);
      setFeedback({
        isCorrect: true,
        message: 'æ­£è§£ï¼'
      });
    } else {
      setFeedback({
        isCorrect: false,
        message: `ä¸æ­£è§£... æ­£è§£ã¯ã€Œ${currentQuestion.correct}ã€ã§ã™ã€‚`
      });
    }
    setIsAnswered(true);
  };

  // æ¬¡ã®å•é¡Œã¸é€²ã‚€
  const handleNextQuestion = () => {
    const nextQuestion = currentQuestionIndex + 1;
    if (nextQuestion < questions.length) {
      setCurrentQuestionIndex(nextQuestion);
    } else {
      setShowScore(true);
    }
  };

  // ã‚«ãƒ†ã‚´ãƒªãƒ¼é¸æŠç”»é¢ã«æˆ»ã‚‹
  const handleBackToCategorySelect = () => {
    setSelectedCategory(null);
    setSelectedSubcategory(null);
    setQuestions([]);
    setFeedback(null);
    setIsAnswered(false);
    setIsQuizKingMode(false);
    setShowRanking(false);
    setAnswerHistory([]);
  };

  // ã‚µãƒ–ã‚«ãƒ†ã‚´ãƒªãƒ¼é¸æŠç”»é¢ã‚’å‰Šé™¤ã—ã€ã‚«ãƒ†ã‚´ãƒªãƒ¼é¸æŠç”»é¢ã®ã¿è¡¨ç¤º
  if (!selectedCategory && !isQuizKingMode) {
    return (
      <div className="app">
        <h1>ã‚¯ã‚¤ã‚ºã‚¢ãƒ—ãƒª</h1>
        <div className="category-selection">
          <div className="quiz-king-section">
            <h2>ã‚¯ã‚¤ã‚ºç‹ãƒãƒ£ãƒ¬ãƒ³ã‚¸</h2>
            <p>å…¨ã‚«ãƒ†ã‚´ãƒªãƒ¼ã‹ã‚‰30å•ã®ãƒ©ãƒ³ãƒ€ãƒ å•é¡Œã«æŒ‘æˆ¦ï¼</p>
            <button
              onClick={startQuizKingChallenge}
              className="quiz-king-button"
            >
              ã‚¯ã‚¤ã‚ºç‹ã«æŒ‘æˆ¦ã™ã‚‹
            </button>
          </div>
          <div className="category-section">
            <h2>ã‚«ãƒ†ã‚´ãƒªãƒ¼ã‚’é¸æŠã—ã¦ãã ã•ã„</h2>
            <div className="category-grid">
              {Object.entries(quizData.categories).map(([key, category]) => (
                <button
                  key={key}
                  onClick={() => setSelectedCategory(key)}
                  className="category-button"
                >
                  {category.name}
                </button>
              ))}
            </div>
          </div>
        </div>
      </div>
    );
  }

  // ãƒ‡ãƒ¼ã‚¿ãŒèª­ã¿è¾¼ã¾ã‚Œã¦ã„ãªã„å ´åˆã®ãƒ­ãƒ¼ãƒ‡ã‚£ãƒ³ã‚°è¡¨ç¤º
  if (questions.length === 0) {
    return (
      <div className="app">
        <div className="loading">
          <h2>å•é¡Œã‚’èª­ã¿è¾¼ã‚“ã§ã„ã¾ã™...</h2>
        </div>
      </div>
    );
  }

  // ã‚¯ã‚¤ã‚ºç”»é¢
  return (
    <div className="app">
      {showScore ? (
        <div className="score-section">
          <h2>ã‚¯ã‚¤ã‚º{isQuizKingMode ? "ç‹ãƒãƒ£ãƒ¬ãƒ³ã‚¸" : ""}çµ‚äº†ï¼</h2>
          <div className="score-text">
            ã‚ãªãŸã®ã‚¹ã‚³ã‚¢ã¯ {score} / {questions.length} ã§ã™
            {isQuizKingMode && score >= 25 && (
              <div className="quiz-king-achievement">
                ğŸ† ãŠã‚ã§ã¨ã†ï¼ã‚ãªãŸã¯ã‚¯ã‚¤ã‚ºç‹ã®ç§°å·ã‚’ç²å¾—ã—ã¾ã—ãŸï¼
              </div>
            )}
          </div>

          <div className="answer-history-section">
            <h3>å›ç­”å±¥æ­´</h3>
            <div className="answer-history-list">
              {answerHistory.map((answer, index) => (
                <div key={index} className={`answer-history-item ${answer.isCorrect ? 'correct' : 'incorrect'}`}>
                  <div className="answer-history-header">
                    <span className="question-number">å•é¡Œ {answer.questionNumber}</span>
                    <span className="category-level">{answer.category} - {answer.subcategoryName}</span>
                  </div>
                  <div className="question">{answer.question}</div>
                  <div className="answer-details">
                    <div className="selected-answer">
                      ã‚ãªãŸã®å›ç­”: <span className={answer.isCorrect ? 'correct-text' : 'incorrect-text'}>
                        {answer.selectedAnswer}
                      </span>
                    </div>
                    {!answer.isCorrect && (
                      <div className="correct-answer">
                        æ­£è§£: <span className="correct-text">{answer.correctAnswer}</span>
                      </div>
                    )}
                  </div>
                </div>
              ))}
            </div>
          </div>

          {!showRanking ? (
            <div className="name-input-section">
              <input
                type="text"
                placeholder="ã‚ãªãŸã®åå‰ã‚’å…¥åŠ›ã—ã¦ãã ã•ã„"
                value={playerName}
                onChange={(e) => setPlayerName(e.target.value)}
                className="name-input"
              />
              <button
                onClick={saveScore}
                className="save-score-button"
                disabled={!playerName.trim()}
              >
                ã‚¹ã‚³ã‚¢ã‚’ä¿å­˜
              </button>
            </div>
          ) : (
            <div className="ranking-section">
              <h3>ãƒ©ãƒ³ã‚­ãƒ³ã‚°</h3>
              <div className="ranking-tabs">
                <button
                  className={`ranking-tab ${!isQuizKingMode ? 'active' : ''}`}
                  onClick={() => setIsQuizKingMode(false)}
                >
                  é€šå¸¸ãƒ¢ãƒ¼ãƒ‰
                </button>
                <button
                  className={`ranking-tab ${isQuizKingMode ? 'active' : ''}`}
                  onClick={() => setIsQuizKingMode(true)}
                >
                  ã‚¯ã‚¤ã‚ºç‹ãƒ¢ãƒ¼ãƒ‰
                </button>
              </div>
              <div className="ranking-list">
                {rankings[isQuizKingMode ? 'quizKing' : 'normal'].map((rank, index) => (
                  <div key={index} className="ranking-item">
                    <span className="rank-number">#{index + 1}</span>
                    <span className="player-name">{rank.name}</span>
                    <span className="player-score">{rank.score}/{rank.total}</span>
                    <div className="rank-details">
                      <span>{rank.category}</span>
                      <span>{rank.date}</span>
                    </div>
                  </div>
                ))}
                {rankings[isQuizKingMode ? 'quizKing' : 'normal'].length === 0 && (
                  <div className="no-ranking">
                    ã¾ã ãƒ©ãƒ³ã‚­ãƒ³ã‚°ãƒ‡ãƒ¼ã‚¿ãŒã‚ã‚Šã¾ã›ã‚“
                  </div>
                )}
              </div>
            </div>
          )}

          <div className="button-group">
            <button
              onClick={handleBackToCategorySelect}
              className="back-button"
            >
              {isQuizKingMode ? "ãƒˆãƒƒãƒ—ç”»é¢" : "ã‚«ãƒ†ã‚´ãƒªãƒ¼é¸æŠ"}ã«æˆ»ã‚‹
            </button>
          </div>
        </div>
      ) : (
        <>
          <div className="question-section">
            <div className="question-header">
              <div className="question-count">
                <span>è³ªå• {currentQuestionIndex + 1}</span>/{questions.length}
              </div>
              <div className="category-name">
                {isQuizKingMode ? 
                  `${questions[currentQuestionIndex].categoryName} - ${questions[currentQuestionIndex].subcategoryName}` :
                  `${quizData.categories[selectedCategory].name} - ${questions[currentQuestionIndex].subcategoryName}`
                }
              </div>
