import React, { useState, useEffect } from 'react';
import quizData from './quizData.json';
import './styles.css';

// 選択肢をランダムに並べ替える関数
function shuffleArray(array) {
  return array.sort(() => 0.5 - Math.random());
}

// 全カテゴリ・全難易度から問題を取得する関数
function getAllQuestions() {
  const allQuestions = [];
  Object.entries(quizData.categories).forEach(([categoryKey, category]) => {
    Object.entries(category.levels).forEach(([levelKey, level]) => {
      const questionsWithMeta = level.questions.map(q => ({
        ...q,
        category: category.name,
        level: level.name
      }));
      allQuestions.push(...questionsWithMeta);
    });
  });
  return shuffleArray(allQuestions).slice(0, 30);
}

function QuizApp() {
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [selectedLevel, setSelectedLevel] = useState(null);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [score, setScore] = useState(0);
  const [showScore, setShowScore] = useState(false);
  const [options, setOptions] = useState([]);
  const [questions, setQuestions] = useState([]);
  const [feedback, setFeedback] = useState(null);
  const [isAnswered, setIsAnswered] = useState(false);
  const [isQuizKingMode, setIsQuizKingMode] = useState(false);
  const [playerName, setPlayerName] = useState('');
  const [showRanking, setShowRanking] = useState(false);
  const [rankings, setRankings] = useState({
    normal: [],
    quizKing: []
  });

  // ランキングデータを読み込む
  useEffect(() => {
    const savedRankings = localStorage.getItem('quizRankings');
    if (savedRankings) {
      setRankings(JSON.parse(savedRankings));
    }
  }, []);

  // スコアを保存する関数
  const saveScore = () => {
    if (!playerName.trim()) return;

    const newRanking = {
      name: playerName,
      score: score,
      total: questions.length,
      date: new Date().toLocaleDateString(),
      category: isQuizKingMode ? 'クイズ王チャレンジ' : quizData.categories[selectedCategory].name,
      level: isQuizKingMode ? '全レベル' : quizData.categories[selectedCategory].levels[selectedLevel].name
    };

    const updatedRankings = {
      ...rankings,
      [isQuizKingMode ? 'quizKing' : 'normal']: [
        ...rankings[isQuizKingMode ? 'quizKing' : 'normal'],
        newRanking
      ].sort((a, b) => (b.score / b.total) - (a.score / a.total)).slice(0, 10)
    };

    setRankings(updatedRankings);
    localStorage.setItem('quizRankings', JSON.stringify(updatedRankings));
    setShowRanking(true);
  };

  // クイズ王チャレンジモード開始
  const startQuizKingChallenge = () => {
    const allQuestions = getAllQuestions();
    setQuestions(allQuestions);
    setCurrentQuestionIndex(0);
    setScore(0);
    setShowScore(false);
    setFeedback(null);
    setIsAnswered(false);
    setIsQuizKingMode(true);
  };

  // カテゴリーとレベルが選択されたときに問題を設定
  useEffect(() => {
    if (selectedCategory && selectedLevel && quizData.categories[selectedCategory]) {
      try {
        const categoryQuestions = quizData.categories[selectedCategory].levels[selectedLevel].questions;
        if (categoryQuestions && categoryQuestions.length > 0) {
          const shuffledQuestions = shuffleArray([...categoryQuestions]).slice(0, 10);
          setQuestions(shuffledQuestions);
          setCurrentQuestionIndex(0);
          setScore(0);
          setShowScore(false);
          setFeedback(null);
          setIsAnswered(false);
          setIsQuizKingMode(false);
        } else {
          console.error('選択されたカテゴリーとレベルに問題がありません');
        }
      } catch (error) {
        console.error('問題データの読み込みエラー:', error);
      }
    }
  }, [selectedCategory, selectedLevel]);

  // 問題が切り替わるたびに選択肢の順序をシャッフル
  useEffect(() => {
    if (questions.length > 0 && currentQuestionIndex < questions.length) {
      const currentQuestion = questions[currentQuestionIndex];
      if (currentQuestion && currentQuestion.correct && currentQuestion.distractors) {
        const allOptions = shuffleArray([currentQuestion.correct, ...currentQuestion.distractors]);
        setOptions(allOptions);
        setFeedback(null);
        setIsAnswered(false);
      }
    }
  }, [currentQuestionIndex, questions]);

  // 回答ボタンをクリックしたときの処理
  const handleAnswerOptionClick = (selectedOption) => {
    if (!questions[currentQuestionIndex] || isAnswered) return;

    const currentQuestion = questions[currentQuestionIndex];
    const isCorrect = selectedOption === currentQuestion.correct;

    if (isCorrect) {
      setScore(prevScore => prevScore + 1);
      setFeedback({
        isCorrect: true,
        message: '正解！'
      });
    } else {
      setFeedback({
        isCorrect: false,
        message: `不正解... 正解は「${currentQuestion.correct}」です。`
      });
    }
    setIsAnswered(true);
  };

  // 次の問題へ進む
  const handleNextQuestion = () => {
    const nextQuestion = currentQuestionIndex + 1;
    if (nextQuestion < questions.length) {
      setCurrentQuestionIndex(nextQuestion);
    } else {
      setShowScore(true);
    }
  };

  // レベル選択画面に戻る
  const handleBackToLevelSelect = () => {
    setSelectedLevel(null);
    setQuestions([]);
    setFeedback(null);
    setIsAnswered(false);
    setIsQuizKingMode(false);
  };

  // カテゴリー選択画面に戻る
  const handleBackToCategorySelect = () => {
    setSelectedCategory(null);
    setSelectedLevel(null);
    setQuestions([]);
    setFeedback(null);
    setIsAnswered(false);
    setIsQuizKingMode(false);
  };

  // カテゴリー選択画面
  if (!selectedCategory && !isQuizKingMode) {
    return (
      <div className="app">
        <h1>クイズアプリ</h1>
        <div className="category-selection">
          <div className="quiz-king-section">
            <h2>クイズ王チャレンジ</h2>
            <p>全カテゴリー・全難易度から30問のランダム問題に挑戦！</p>
            <button
              onClick={startQuizKingChallenge}
              className="quiz-king-button"
            >
              クイズ王に挑戦する
            </button>
          </div>
          <div className="category-section">
            <h2>カテゴリーを選択してください</h2>
            <div className="category-grid">
              {Object.entries(quizData.categories).map(([key, category]) => (
                <button
                  key={key}
                  onClick={() => setSelectedCategory(key)}
                  className="category-button"
                >
                  {category.name}
                </button>
              ))}
            </div>
          </div>
        </div>
      </div>
    );
  }

  // レベル選択画面
  if (!selectedLevel && !isQuizKingMode) {
    return (
      <div className="app">
        <h1>難易度選択</h1>
        <div className="level-selection">
          <h2>{quizData.categories[selectedCategory].name}</h2>
          <div className="level-grid">
            {Object.entries(quizData.categories[selectedCategory].levels).map(([level, levelData]) => (
              <button
                key={level}
                onClick={() => setSelectedLevel(level)}
                className={`level-button level-${level}`}
              >
                {levelData.name}
              </button>
            ))}
          </div>
          <button
            onClick={handleBackToCategorySelect}
            className="back-button"
          >
            カテゴリー選択に戻る
          </button>
        </div>
      </div>
    );
  }

  // データが読み込まれていない場合のローディング表示
  if (questions.length === 0) {
    return (
      <div className="app">
        <div className="loading">
          <h2>問題を読み込んでいます...</h2>
        </div>
      </div>
    );
  }

  // クイズ画面
  return (
    <div className="app">
      {showScore ? (
        <div className="score-section">
          <h2>クイズ{isQuizKingMode ? "王チャレンジ" : ""}終了！</h2>
          <div className="score-text">
            あなたのスコアは {score} / {questions.length} です
            {isQuizKingMode && score >= 25 && (
              <div className="quiz-king-achievement">
                🏆 おめでとう！あなたはクイズ王の称号を獲得しました！
              </div>
            )}
          </div>

          {!showRanking ? (
            <div className="name-input-section">
              <input
                type="text"
                placeholder="あなたの名前を入力してください"
                value={playerName}
                onChange={(e) => setPlayerName(e.target.value)}
                className="name-input"
              />
              <button
                onClick={saveScore}
                className="save-score-button"
                disabled={!playerName.trim()}
              >
                スコアを保存
              </button>
            </div>
          ) : (
            <div className="ranking-section">
              <h3>ランキング</h3>
              <div className="ranking-tabs">
                <button
                  className={`ranking-tab ${!isQuizKingMode ? 'active' : ''}`}
                  onClick={() => setIsQuizKingMode(false)}
                >
                  通常モード
                </button>
                <button
                  className={`ranking-tab ${isQuizKingMode ? 'active' : ''}`}
                  onClick={() => setIsQuizKingMode(true)}
                >
                  クイズ王モード
                </button>
              </div>
              <div className="ranking-list">
                {rankings[isQuizKingMode ? 'quizKing' : 'normal'].map((rank, index) => (
                  <div key={index} className="ranking-item">
                    <span className="rank-number">#{index + 1}</span>
                    <span className="player-name">{rank.name}</span>
                    <span className="player-score">{rank.score}/{rank.total}</span>
                    <div className="rank-details">
                      <span>{rank.category} - {rank.level}</span>
                      <span>{rank.date}</span>
                    </div>
                  </div>
                ))}
                {rankings[isQuizKingMode ? 'quizKing' : 'normal'].length === 0 && (
                  <div className="no-ranking">
                    まだランキングデータがありません
                  </div>
                )}
              </div>
            </div>
          )}

          <div className="button-group">
            {!isQuizKingMode && (
              <button
                onClick={handleBackToLevelSelect}
                className="back-button"
              >
                難易度選択に戻る
              </button>
            )}
            <button
              onClick={handleBackToCategorySelect}
              className="back-button"
            >
              {isQuizKingMode ? "トップ画面" : "カテゴリー選択"}に戻る
            </button>
          </div>
        </div>
      ) : (
        <>
          <div className="question-section">
            <div className="question-header">
              <div className="question-count">
                <span>質問 {currentQuestionIndex + 1}</span>/{questions.length}
              </div>
              <div className="category-name">
                {isQuizKingMode ? (
                  `${questions[currentQuestionIndex].category} - ${questions[currentQuestionIndex].level}`
                ) : (
                  `${quizData.categories[selectedCategory].name} - 
                  ${quizData.categories[selectedCategory].levels[selectedLevel].name}`
                )}
              </div>
            </div>
            <div className="question-text">
              {questions[currentQuestionIndex].question}
            </div>
            {feedback && (
              <div className={`feedback ${feedback.isCorrect ? 'correct' : 'incorrect'}`}>
                {feedback.message}
              </div>
            )}
          </div>
          <div className="answer-section">
            {options.map((option, index) => (
              <button 
                key={index} 
                onClick={() => handleAnswerOptionClick(option)}
                className={`answer-button ${
                  isAnswered
                    ? option === questions[currentQuestionIndex].correct
                      ? 'correct'
                      : 'incorrect'
                    : ''
                }`}
                disabled={isAnswered}
              >
                {option}
              </button>
            ))}
          </div>
          {isAnswered && (
            <div className="next-button-container">
              <button 
                onClick={handleNextQuestion}
                className="next-button"
              >
                {currentQuestionIndex === questions.length - 1 ? '結果を見る' : '次の問題へ'}
              </button>
            </div>
          )}
        </>
      )}
    </div>
  );
}

export default QuizApp;
